mapper.select:
SELECT * FROM ofboard
WHERE board_folder_no=#{boardFolderNo}
ORDER BY board_no desc

SELECT * FROM ofboard
WHERE board_folder_no=#{boardFolderNo}

ORDER BY board_no desc

mapper.update:
UPDATE ofboard
SET board_hits=board_hits+1
WHERE board_no=#{boardNo}

UPDATE ofboard
SET board_title=#{boardTitle}, board_content=#{boardContent:CLOB}, board_date=sysdate
WHERE board_no=#{boardNo}
